import React, { useState, useRef, useEffect } from "react";
import axios from "axios";
import "./App.css";
import Markdown from "react-markdown";

const FileUpload = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [data, setData] = useState("");
  const [query, setQuery] = useState("");
  const [response, setResponse] = useState("");
  const [collectionName, setCollectionName] = useState("");
  const [pdfUploaded, setPdfUploaded] = useState(false);
  const [uploading, setUploading] = useState(false);
  const [chatHistory, setChatHistory] = useState([]);
  const chatEndRef = useRef(null);

  useEffect(() => {
    scrollToBottom();
  }, [response]);

  const scrollToBottom = () => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  const handleFileChange = async (event) => {
    const file = event.target.files[0];
    setSelectedFile(file);
    await uploadFile(file);
  };

  const uploadFile = async (file) => {
    setUploading(true);
    const formData = new FormData();
    formData.append("pdf_file", file);

    try {
      const response = await axios.post(
        "https://paperchat-api.onrender.com/upload/",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );
      setData(response.data);
      console.log("File uploaded successfully:", response.data);
      setCollectionName(file.name.split(".").slice(0, -1).join("."));
      setPdfUploaded(true);
    } catch (error) {
      console.error("Error uploading file:", error);
    } finally {
      setUploading(false);
    }
  };

  const handleQueryChange = (event) => {
    setQuery(event.target.value);
  };

  const handleQuery = async () => {
    if (!query) return;

    const newChatHistory = [...chatHistory, { type: "user", text: query }];
    setChatHistory(newChatHistory);
    setQuery("");
    setResponse("");

    try {
      const response = await fetch(
        "https://paperchat-api.onrender.com/query/",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            query: query,
            collection_name: collectionName,
          }),
        }
      );

      if (!response.body) {
        console.error("ReadableStream not supported in this browser.");
        return;
      }

      const reader = response.body.getReader();
      const decoder = new TextDecoder();
      let result = "";
      let currentBotResponse = "";

      while (true) {
        const { done, value } = await reader.read();
        if (done) break;
        const chunk = decoder.decode(value);
        result += chunk;
        currentBotResponse += chunk;

        setResponse(result);

        setChatHistory((prevChatHistory) => {
          const lastEntry = prevChatHistory[prevChatHistory.length - 1];
          if (lastEntry && lastEntry.type === "bot") {
            lastEntry.text = currentBotResponse;
            return [...prevChatHistory];
          } else {
            return [...prevChatHistory, { type: "bot", text: chunk }];
          }
        });

        await new Promise((resolve) => setTimeout(resolve, 20));
      }

      // Scroll to the bottom after receiving response
      scrollToBottom();
    } catch (error) {
      console.error("Error querying database:", error);
    }
  };

  return (
    <div className="main">
      <div className="header">
        <img
          src="https://proxmaq.com/wp-content/uploads/2023/03/AI_Planet-logo.jpg"
          alt="AI Planet Logo"
        />
        <h1>Welcome to AI Planet</h1>
        <input type="file" onChange={handleFileChange} />
        {pdfUploaded ? (
          <p>PDF Uploaded Successfully</p>
        ) : (
          <p>Select a PDF file to upload</p>
        )}
      </div>
      <div className="chat-container">
        <div className="chat-history">
          {chatHistory.map((message, index) => (
            <div key={index} className={`message ${message.type}`}>
              <p>{message.text}</p>
            </div>
          ))}
          <div ref={chatEndRef} />
        </div>
        <div className="chat-input">
          <input
            type="text"
            placeholder="Ask a question..."
            value={query}
            onChange={handleQueryChange}
          />
          <button onClick={handleQuery}>Send</button>
        </div>
      </div>
      {response && (
        <div className="response-container">
          <Markdown>{response}</Markdown>
        </div>
      )}
    </div>
  );
};

export default FileUpload;
